package exercisesJava.dataTypes;

public class Exercise3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Write a Java program to declare three variable a, b, & c
		// and store the values respectively 10, true & JavaCode .
		// Then display their values on the screen.
		
		int a = 10;
		boolean b = true;
		String c = "JavaCode";
		System.out.print(Integer.toString(a) + "\n");
		System.out.print(Boolean.toString(b) + "\n");
		System.out.print(c + "\n");

	}
	
}
